$(document).ready((function(){"use strict";document.querySelectorAll(".password-showHide").forEach((item=>{item.addEventListener("click",(()=>{item.classList.toggle("hide"),"password"===item.closest(".form-input").querySelector("input").type?item.closest(".form-input").querySelector("input").type="text":item.closest(".form-input").querySelector("input").type="password"}))}))}));